mapper.select:
SELECT contractno, lender, borrower, `type`, goods, startdate, duedate, imageurl, audiourl, description, agreed, status
FROM contract
WHERE contractno = #{contractno}

mapper.update:
UPDATE contract SET status = 2 WHERE contractno = #{contractno};
UPDATE user SET lent = lent+1 WHERE id = (
    SELECT lender FROM contract WHERE contractno = #{contractno}
);
UPDATE user SET borrowed = borrowed+1 WHERE id = (
    SELECT borrower FROM contract WHERE contractno = #{contractno}
);

UPDATE contract SET status = 3 WHERE contractno = #{contractno};
UPDATE user SET lent = lent-1 WHERE id = (
    SELECT lender FROM contract WHERE contractno = #{contractno}
);
UPDATE user SET borrowed = borrowed-1 WHERE id = (
    SELECT borrower FROM contract WHERE contractno = #{contractno}
);
